var link=document.querySelector(".js-open-feedback"),modal=document.querySelector(".modal"),close=modal.querySelector(".modal-close"),form=modal.querySelector(".feedback-form"),user=form.querySelector("[name=user]"),mail=form.querySelector("[name=mail]"),message=form.querySelector("[name=message]");link.addEventListener("click",function(e){e.preventDefault(),modal.classList.add("modal-show"),user.focus()}),close.addEventListener("click",function(e){e.preventDefault(),modal.classList.remove("modal-show"),modal.classList.remove("modal-error")}),form.addEventListener("submit",function(e){user.value&&mail.value&&message.value||(e.preventDefault(),modal.classList.add("modal-error"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&modal.classList.contains("modal-show")&&(e.preventDefault(),modal.classList.remove("modal-show"),modal.classList.remove("modal-error"))});